<script>
import axios from "axios";
import moment from "moment";
import jsPDF from "jspdf";
import autoTable from "jspdf-autotable";
import html2canvas from "html2canvas";

export default {
  data() {
    return {
      dialog: false,
      dialogAmort: false,
      search: "",
      loans: [],
      headers: [
        { text: "CI Afiliado", value: "affiliate.identity_card" },
        { text: "N° de Préstamo", value: "code" },
        { text: "Comprobante", value: "voucher" },
        { text: "Fecha Solicitud", value: "request_date" },
        { text: "Monto Solicitado", value: "amount_requested" },
        { text: "Meses", value: "loan_term" },
        { text: "Amortizacion", value: "amortization" },
        { text: "Interes", value: "interest_rate" },
        { text: "Tipo", value: "type" },
        { text: "Opciones", value: "actions", sortable: false },
      ],
      editedIndex: -1,
      _id:'',
      affiliate_identity_card:'',
      affiliate_last_name:'',
      affiliate_mothers_last_name:'',
      affiliate_name:'',

      dataNow:'',
      
      code: '',
      voucher: '',
      request_date: '',
      amount_requested: '',
      loan_term: '',
      amortization: '',
      interest_rate: '',
      type: '',

      payments: [],
      headersPayments: [
        { text: "Nro.", value: "index" },
        { text: "Fecha de transacción", value: "date" },
        { text: "Monto Mensual", value: "estimated_quota" },
        { text: "Amortización", value: "quota" },
        { text: "Otros Cobros", value: "other_charge" },
        { text: "Interes", value: "interest" },
        { text: "D/C", value: "d_c" },
        { text: "Interes Penal", value: "accumulated_interest" },
        { text: "Cuota Mensual", value: "total" },
        { text: "Saldo Capital", value: "balance" },
        { text: "Recibo", value: "receipt" },
        { text: "Cuota Nro", value: "registry" },
        
      ],
      
      date:'',
      total:'',
      receipt:'',
      registry:'',
      
      editPayments:0,
      valid:0,
      validationMessage:[],
      kardexModal:0

    };
  },
  computed: {
    formTitle() {
      return "Editar Trámite";
    },
    itemsWithIndex () {
    return this.payments.map(
      (payments, index) => ({
        ...payments,
        index: index + 1,        
      }))
  }

  },

  watch: {
    dialog(val) {
      val || this.close();
    },
  },

  created() {
    this.toList();
  },

  methods: {

    toList() {
      let me = this;
      let header = {"token": this.$store.state.token}
      let config = {headers: header}
      axios
        .get("loan/list", config)
        .then(function (response) {
          me.loans = response.data;
        })
        .catch(function (error) {
          console.log(error);
        });
    },

    validate () {
      this.valid = 0;
      this.validationMessage= [];

      if (this.validationMessage.length){
        this.valid = 1;
      }
      return this.valid;
    },

    listPayments(id){
      
      let me = this;
      let header = {"token": this.$store.state.token}
      let config = {headers: header}          
      axios
        .get('payment/list?loan_id='+id,config)
        .then(function (response){
          me.payments=response.data;
          me.payments.map(
          (payments, index) => ({
            ...payments,
            index: index + 1
          }));
        })
        .catch(function(error){
            console.log(error);
        });
    },

    showPayments(item){
        this.clear();
        this._id = item._id;
        this.affiliate_identity_card = item.affiliate.identity_card;
        this.affiliate_last_name = item.affiliate.last_name;
        this.affiliate_mothers_last_name = item.affiliate.mothers_last_name;
        this.affiliate_name = item.affiliate.name;

        this.code = item.code;
        this.voucher = item.voucher;
        this.request_date = item.request_date;
        this.amount_requested = item.amount_requested;
        this.loan_term = item.loan_term;
        this.amortization = item.amortization;
        this.interest_rate = item.interest_rate;
        this.type = item.type;
        
        this.listPayments(item._id);
        this.editPayments = 1;
    },
    hideEditPayments(){
        this.editPayments = 0;
        this.clear();
    },
    saveAmort() {
      let me = this;
      let header = {"token": this.$store.state.token}
      let config = {headers: header}
      if (this.validate()){
        return;
      }
        axios.post('payment/add',{
          'loan':this._id,
          'date':this.date,
          'total':this.total,
          'receipt':this.receipt,
          'registry':this.registry}, config)
          .then(function(response){
            me.close();
            me.toList();
          })
          .catch(function(error){
            console.log(error);
          });

      this.hideEditPayments();
    },
    saveLoan() {
      let me = this;
      let header = {"token": this.$store.state.token}
      let config = {headers: header}
      if (this.validate()){
        return;
      }
      if (this.editedIndex > -1) {
        axios.put('loan/update',{
          '_id':this._id,
          'code':this.code,
          'voucher':this.voucher,
          'request_date':this.request_date,
          'amount_requested':this.amount_requested,
          'loan_term':this.loan_term,
          'amortization':this.amortization,
          'interest_rate':this.interest_rate}, config)
          .then(function(response){
            me.clear();
            me.close();
            me.toList();
          })
          .catch(function(error){
            console.log(error);
          });
      } else {
        //
      }
      this.close();
    },
    addInt(id) {
      console.log(id)
      let me = this;
      let header = {"token": this.$store.state.token}
      let config = {headers: header}
      if (this.validate()){
        return;
      }
      axios.get('loan/queryint?_id='+id,config)
        .then(function(response){
          me.clear();
          me.close();
          me.toList();
        })
        .catch(function(error){
          console.log(error);
        });
        this.editPayments = 0;
        this.clear();
    },
    addAccint(id) {
      let me = this;
      let header = {"token": this.$store.state.token}
      let config = {headers: header}
      if (this.validate()){
        return;
      }
      axios.get('loan/queryaccint?_id='+id,config)
        .then(function(response){
          me.clear();
          me.close();
          me.toList();
        })
        .catch(function(error){
          console.log(error);
        });
        this.editPayments = 0;
        this.clear();
    },

    editLoan(item) {
      this._id=item._id;
      this.code = item.code;
      this.voucher = item.voucher;
      this.request_date = item.request_date;
      this.amount_requested = item.amount_requested;
      this.loan_term = item.loan_term;
      this.amortization = item.amortization;
      this.interest_rate = item.interest_rate;
      this.type = item.type;
      this.editedIndex=1;
      this.dialog = true;
    },

    clear() {
      this._id='';
      this.code = '';
      this.voucher = '';
      this.request_date = '';
      this.amount_requested = '';
      this.loan_term = '';
      this.amortization = '';
      this.interest_rate = '';
      this.type = '';
      this.type = '';
      this.valid ='';
      this.validationMessage =[];
      this.editedIndex=-1;
    },

    close() {
      this.dialog = false;
      this.dialogAmort = false;
      
    },
    
    showKardex(item){
        this.clear();

        this.dataNow = moment(new Date()).local().format('DD-MM-YYYY - HH:mm');
        this.affiliate_identity_card = item.affiliate.identity_card;
        this.affiliate_last_name = item.affiliate.last_name;
        this.affiliate_mothers_last_name = item.affiliate.mothers_last_name;
        this.affiliate_name = item.affiliate.name;

        this.code = item.code;
        this.voucher = item.voucher;
        this.request_date = moment(item.request_date).format('YYYY-MM-DD');
        this.amount_requested = item.amount_requested;
        this.loan_term = item.loan_term;
        this.amortization = item.amortization;
        this.interest_rate = item.interest_rate;
        this.type = item.type;
        
        this.listPayments(item._id);
      this.kardexModal = 1;
    },
    hideKardex(){
      this.kardexModal = 0;
    },

    createPrint(){

      var quotes = document.getElementById('kardex');
      html2canvas(quotes).then(function (canvas) {
          var imgData = canvas.toDataURL('image/png');
          var imgWidth = 210;
          var pageHeight = 295;

          var imgHeight = canvas.height * imgWidth / canvas.width;
          var heightLeft = imgHeight;
          
          var doc = new jsPDF('p', 'mm', 'a4');
          var position = 0;

          doc.addImage(imgData, 'PNG', 0, position, imgWidth, imgHeight);
          heightLeft -= pageHeight;

          while (heightLeft >= 0) {
              position = heightLeft - imgHeight;
              doc.addPage();
              doc.addImage(imgData, 'PNG', 0, position, imgWidth, imgHeight);
              heightLeft -= pageHeight;
          }
          doc.save('kardex.pdf');
      });
    }
  },
};
